cias\"><?php $m->pContent(\"noticias\"); ?></a>\r\n                    </li>\r\n                    <li <?php if (($this->url() != BASE_URL . $language->url) && ($this->slug() == \'contatos\')) echo \'class=\"active\"\' ?>>\r\n                        <a href=\"<?php echo URL_PUBLIC . $language->url; ?>contatos\"><?php $m->pContent(\"contatos\"); ?></a>\r\n                    </li>\r\n                </ul>\r\n            </div>\r\n            <!-- END MENU -->\r\n\r\n        </section>\r\n        <!-- END HEADER -->\r\n\r\n        <!-- CONTAINER -->\r\n        <section id=\"container\">\r\n\r\n            <!-- :::::::::::::::::::::::::::::::::::: SNIPPETS ::::::::::::::::::::::::::::::::::::::: -->\r\n            <?php\r\n            if ($this->url() == BASE_URL . $language->url) {\r\n                $this->includeSnippet(\'home\');\r\n            } else {\r\n                if ($this->slug() == \"apresentacao\") {\r\n                    $this->includeSnippet(\'apresentacao\');\r\n                } elseif ($this->slug() == \"produtos\") {\r\n                    $this->includeSnippet(\'categorias\');\r\n                } elseif ($this->parent() != NULL && $this->parent()->slug() == \"produtos\") {\r\n                    $this->includeSnippet(\'produtos\');\r\n                } elseif (($this->parent() != NULL) && ($this->parent()->parent() != NULL) && ($this->parent()->parent()->slug() == \'produtos\')) {\r\n                    $this->includeSnippet(\'produto\');\r\n                } elseif ($this->slug() == \"portefolio\") {\r\n                    $this->includeSnippet(\'portefolio\');\r\n                } elseif ($this->parent()->slug() == \"portefolio\") {\r\n                    $this->includeSnippet(\'port\');\r\n                } elseif ($this->slug() == \"parcerias\") {\r\n                    $this->includeSnippet(\'parcerias\');\r\n                } elseif ($this->slug() == \"assistencia\") {\r\n                    $this->includeSnippet(\'assistencia\');\r\n   